import * as THREE from 'three';
import Stats from 'stats.js';
import dat from 'dat.gui';

// ----- 주제:  GUI control
// 설치필요!: npm i dat.gui

export default function example() {

	// html에서 캔버스 가져와서 사용하기
	const canvas = document.querySelector('#three-canvas');
	const renderer = new THREE.WebGLRenderer({
		canvas,
    antialias: true,
	});
	renderer.setSize(window.innerWidth, window.innerHeight);
	renderer.setPixelRatio(devicePixelRatio > 1 ? 2:1);

	// Scene
	const scene = new THREE.Scene();

	// Camera
	// Perspective Camera(원근 카메라)
	const camera = new THREE.PerspectiveCamera(
		75, // 시야각(field of view)
		window.innerWidth / window.innerHeight, // 종횡비(aspect)
		0.1, // near
		1000 // far
	);
	camera.position.y = 1;
	camera.position.z = 5;
	scene.add(camera);

  // Light
  const ambientLight = new THREE.AmbientLight('white', 0.5)
  const directionalLight = new THREE.DirectionalLight('white', 1)
  directionalLight.position.x = 1
  directionalLight.position.z = 2
  scene.add(ambientLight)
  scene.add(directionalLight)

	// Mesh
	const geometry = new THREE.BoxGeometry(1, 1, 1);
	const material = new THREE.MeshStandardMaterial({
		color: 'seagreen'
	});
  const mesh = new THREE.Mesh(geometry, material);
	scene.add(mesh)

  // Dat GUI
  // Dat GUI는 three.js와는 무관하며, 오히려 범용적으로 쓰일 수 있음
  const gui = new dat.GUI();
  // GUI로 조정하고자 하는 객체, (조정하고자 하는 객체의) 속성, 최소 범위, 최대 범위 설정, 조정 스텝
  gui.add(mesh.position, 'y', -5, 5, 0.01).name('메쉬의 Y위치')
  // 메서드 체인 형태로 써 줄 수도 있음
  gui
    .add(mesh.position, 'z')
    .min(-10)
    .max(3)
    .step(0.01)
    .name('메쉬의 Z위치');
  // camera의 위치 
  gui.add(camera.position, 'x', -10, 10, 0.01).name('카메라의 X위치')

  camera.lookAt(mesh.position)


	// 그리기
  const clock = new THREE.Clock()

  function draw(){
    const time = clock.getElapsedTime();
    mesh.rotation.y = time;
    camera.lookAt(mesh.position)


    renderer.render(scene, camera);
    renderer.setAnimationLoop(draw)
  }

	// 사용자가 브라우저 크기 조정시 화면 크기 설정하는 함수 정의
	function setSize(){
		window.aspect = innerWidth/innerHeight
		camera.updateProjectionMatrix()
		renderer.setSize(window.innerWidth, window.innerHeight);
		renderer.render(scene, camera)
	}

	// 이벤트
	window.addEventListener('resize', setSize)

  draw()
}
